import { Component, OnInit } from '@angular/core';
import { Sku } from '../modelos/sku.interface';
import { ApiService } from '../service/api.service';
import { FormGroup, FormControl, Validators } from '@angular/forms';

import { Router } from '@angular/router';


@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit {

  nuevoForm = new FormGroup({
    id: new FormControl(''),
    sku: new FormControl(''),
    article: new FormControl(''),
    stl_marca: new FormControl(''),
    model: new FormControl(''),
    department: new FormControl(''),
    clas: new FormControl(''),
    family: new FormControl(''),
    stock: new FormControl(''),
    amount: new FormControl(''),
    discontinued: new FormControl(''),
    create_date: new FormControl(''),
    finish_date: new FormControl('')
  });


  constructor(private api: ApiService, private router: Router) {

  }

  ngOnInit(): void { }

  postForm(form: Sku) {

    this.api.saveSku(form);
  }

  salir() {
    this.router.navigate(['tabla']);
  }

}
<div class="container">

<div class="section">
	<br>
    <form [formGroup]="nuevoForm" (ngSubmit)="postForm(nuevoForm.value)">
        <div class="form-group">
                <label for="exampleFormControlInput1">SKU</label>
                <input type="text" class="form-control"  placeholder="NUMERO DE SKU" formControlName="sku">
        </div>


            <br><br>
            <button type="submit" class="btn btn-primary">Guardar</button>
            <button type="button" class="btn btn-dark" (click)="salir()">Salir</button>

</form>
</div>


</div>
