import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import {Sku} from '../modelos/sku.interface';
import {HttpClient, HttpHeaders} from '@angular/common/http';



@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private urlApi = 'http://localhost:8080/api/skus';

  constructor(private http: HttpClient) { }

  public getData() : Observable<Sku[]> {
    return this.http.get<Sku[]>(this.urlApi);
  }

  public getDataBySku(sku:number) : Observable<any> {
    return this.http.get<any>(this.urlApi+ "/sku/" + sku);
  }

    public deleteproduct (id: number): void{
      let options = {
        headers: new HttpHeaders({
          "Conten-type": "application-json"
        }),
        body: {
          "sku": 1234567,
          "article": "cama",
          "stl_marca": "stilso",
          "model": "spring",
          "department": "hogxzar",
          "clas": 2,
          "family": 1,
          "stock": 10,
          "amount": 200,
          "discontinued": 0,
          "create_date": "2023-12-16",
          "finish_date": null
        }

      }
      this.http.post<any>('http://localhost:8080/api/skus', options);


    }




}
