import { Component, OnInit } from '@angular/core';
import { Sku } from '../modelos/sku.interface';
import { ApiService } from '../service/api.service';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit {

  nuevoForm: FormGroup;
  ocultarD: boolean;
  datosSku: number;


  constructor(private api: ApiService, private router: Router, private formBuilder: FormBuilder, private activerouter: ActivatedRoute) {
    this.ocultarD = false;
    this.datosSku = 0;

    this.nuevoForm = new FormGroup({
      id: new FormControl(''),
      sku: new FormControl('', [Validators.maxLength(6), Validators.required, Validators.minLength(1)]),
      article: new FormControl('', [Validators.maxLength(15), Validators.required, Validators.minLength(1)]),
      stl_marca: new FormControl('', [Validators.maxLength(15), Validators.required, Validators.minLength(1)]),
      model: new FormControl('', [Validators.maxLength(20), Validators.required, Validators.minLength(1)]),
      department: new FormControl('', [Validators.required]),
      clas: new FormControl('', [Validators.required]),
      family: new FormControl('', [Validators.required]),
      stock: new FormControl('', [Validators.maxLength(9), Validators.required, Validators.minLength(1)]),
      amount: new FormControl('', [Validators.maxLength(9), Validators.required, Validators.minLength(1)]),
      discontinued: new FormControl(''),
      create_date: new FormControl(''),
      finish_date: new FormControl('')
    });
    this.nuevoForm.get("sku")?.valueChanges.subscribe(x => {
      console.log('firstname value changed')
      if(x.toString().length==6){
        this.api.getDataBySku(Number(x)).subscribe(data=> {
          if(data.sku>0){
            this.nuevoForm.controls["sku"].setValue("");
            alert("SKU YA EXISTE..");
          }
        });
      }
      console.log(x)
   })
  }


  ngOnInit(): void {
    let skuId = this.activerouter.snapshot.paramMap.get('id');
    if (skuId) {
      this.ocultarD = true;
      this.api.getSkuById(skuId).subscribe(data => {
        this.nuevoForm.setValue({
          id: data.id,
          sku: data.sku,
          article: data.article,
          stl_marca: data.stl_marca,
          model: data.model,
          department: data.department,
          clas: data.clas,
          family: data.family,
          stock: data.stock,
          amount: data.amount,
          discontinued: data.discontinued,
          create_date: data.create_date,
          finish_date: data.finish_date
        });

      })
    }
  }


  postForm(form: Sku) {
    if (form.amount > form.stock) {
      alert("la cantidad no debe ser mayor al stock");
    }
    else if (form.sku.toString() == "") {
      alert("FAVOR DE CAPTURAR UN SKU");
    }
    else if (form.sku.toString().length != 6) {
      alert("FAVOR DE CAPTURAR UN SKU DE 6 DIGITOS");
    }
    else if (form.article.toString() == "") {
      alert("FAVOR DE ESCRIBIR UN ARTICULO...")
    }
    else if (form.stl_marca.toString() == "") {
      alert("FAVOR PONER LA MARCA...")
    }
    else if (form.model.toString() == "") {
      alert("FAVOR DE ESCRIBIR EL MODELO...")
    }
    else if (form.department.toString() == "") {
      alert("FAVOR DE PONER EL DEPARTAMENTO...")
    }
    else if (form.clas.toString() == "") {
      alert("FAVOR DE PONER EL NUMERO DE CLASE...")
    }
    else if (form.family.toString() == "") {
      alert("FAVOR DE PONER EL NUMERO DE FAMILIA...")

    }
    else if (form.stock.toString() == "") {
      alert("FAVOR DE ESCRIBIR UN STOCK...")

    }
    else if (form.amount.toString() == "") {
      alert("FAVOR DE ESCRIBIR UNA CANTIDAD...")
    }
    else {
      if (form.id) {
        this.api.updateSku(form);
      }
      else {
         this.api.saveSku(form);
          this.salir();   
      }



    }

  }

  salir() {
    this.router.navigate(['tabla']);

  }

}

<div class="container">`


<div class="section">
	<br>

    <form [formGroup]="nuevoForm" (ngSubmit)="postForm(nuevoForm.value)">
        <div class="form-group">

                <label for="exampleFormControlInput1">SKU</label>
                <input type="number" class="form-control" placeholder="NUMERO DE SKU" formControlName="sku">
        </div>
        <div class="form-group">
                <label for="exampleFormControlInput1">ARTICULO</label>
                <input type="text" class="form-control"  placeholder="NOMBRE DEL ARTICULO" formControlName="article">
            </div>
            <div class="form-group row">
                <div class="col">
                    <label for="exampleFormControlInput1">MARCA</label>
                    <input type="text" class="form-control"  placeholder="NOMBRE DE LA MARCA" formControlName="stl_marca">     
                </div>
                <div class="col">
                    <label for="exampleFormControlInput1">MODELO</label>
                    <input type="text" class="form-control"  placeholder="MODELO" formControlName="model">     
                </div>
                <div class="col">
                    <label for="exampleFormControlInput1">DEPARTAMENTO</label>
                    <input type="text" class="form-control"  placeholder="NOMBRE DE DEPARTAMENTO" formControlName="department">     
                </div>
            </div>
            <div class="form-group row">
                <div class="col">

                  <select>
                        <label for="exampleFormControlInput1">CLASE</label>
                        <option disabled selected="">SELECCIONE UNA CLASE</option>
                        <option selected>01</option>
                        <input type="text" class="form-control"   placeholder="NUMERO DE CLASE" formControlName="clas" >
                    </select>
                </div>
                <div class="col">
                    <label for="exampleFormControlInput1">FAMILIA</label>
                    <input type="text" class="form-control"  placeholder="NUMERO DE FAMILIA" formControlName="family">     
                </div>
                <div class="col">
                    <label for="exampleFormControlInput1">STOCK</label>
                    <input type="number" class="form-control"  placeholder="NUMERO DE STOCK" formControlName="stock">     
                </div>
				<div class="col">
                    <label for="exampleFormControlInput1">CANTIDAD</label>
                    <input type="number" class="form-control"  placeholder="CANTIDAD TOTAL" formControlName="amount">     
                </div>
            </div>
			<div class="form-group row">
                <div class="col" *ngIf="ocultarD"> 
                    <label for="exampleFormControlInput1">DESCONTINUADO</label><br>
					<input type="checkbox" name="discontinued" value="1" formControlName="discontinued">  
                </div>

            </div>
            <div class="form-group row">
                <div class="col" *ngIf="ocultarD">
                    <label for="exampleFormControlInput1">FECHA ALTA</label><br>
                    <input type="text" class="form-control"  formControlName="create_date" readonly>   
                </div>
                <div class="col" *ngIf="ocultarD" >
                    <label for="exampleFormControlInput1">FECHA BAJA</label><br>
                    <input type="text" class="form-control" formControlName="finish_date" readonly>  
                </div>

            </div>

            <br><br>
            <button type="submit" class="btn btn-primary">GUARDAR</button>
            <button type="button" class="btn btn-dark" (click)="salir()">Salir</button>

</form>
</div>
</div>
